	      minPt = [mapperBounds[0], mapperBounds[2], mapperBounds[4]];
	      maxPt = [mapperBounds[1], mapperBounds[3], mapperBounds[5]];

	      vec3.transformMat4(minPt, minPt, m_transformMatrix);
