	    m_center = $.extend({}, m_this.gcsToWorld(coordinates, gcs));

	    camera_bounds(m_this.boundsFromZoomAndCenter(
	        m_zoom, m_center, m_rotation, null, ignoreDiscreteZoom), m_rotation);
