	    if (layer !== null && layer !== undefined) {
	      layer._exit();
	      m_this.removeChild(layer);

	      m_this.modified();

	      m_this.geoTrigger(geo_event.layerRemove, {
	        type: geo_event.layerRemove,
	        target: m_this,
	        layer: layer
	      });
	    }

