	  //
	  //  {
	  //    'action': 'zoom',  // an ongoing zoom event
	  //    ...
	  //  }
	  //
	  //  {
	  //    'action': 'rotate',   // an ongoing rotate event
	  //    'origin': {...},      // mouse object at the start of the action
	  //    'delta': {x: *, y: *} // mouse movement since action start
	  //                          // not including the current event
	  //  }
	  //
	  //  {
	  //    'acton': 'select',
	  //    'origin': {...},
	  //    'delta': {x: *, y: *}
	  //  }
	  //
	  //  {
