				var tmpNode = this.splitNodeAtPoint( segHighNode, segHighVert, false );
				// move segLowNode to new (lower) trapezoid, if it was the one which was just split
				if ( segHighNode == segLowNode )	segLowNode = tmpNode;
				segHighNode = tmpNode;
