	  /**
	   * Connect mapper to its geometry data
	   *
	   * @param geoms {Array}
	   */
	  ////////////////////////////////////////////////////////////////////////////
	  this.setGeometryDataArray = function (geoms) {
	    if (geoms instanceof Array) {
	      if (m_geomDataArray !== geoms) {
	        m_geomDataArray = [];
	        m_geomDataArray = geoms;
	        this.modified();
	        return true;
	      }
	    } else {
	      console.log('[error] Requies array of geometry data');
	    }

	    return false;
	  };

