	      fiMaterial = vgl.material(), frontPeelShader = null, blendShader = null,
	      finalShader, NUM_PASSES = 6, m_quad = null, fpwidth, fpheight, blwidth, blheight,
	      fiwidth, fiheight, fpopacity, fibackgroundColor;

